package main.conditionalstatements;

public class Loop_operations {
	
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int i=4;
		while (i<=5){
			System.out.println("Hello! while");
			i++;
		}
		
		do {
			System.out.println("Hello! do_while");
		}
		while (i==5);
		
		for(i=6;i<=10;i++) {
			
			System.out.println("Hello! For");
		}

	
	
		
	}

}